// 2. CSV PARSER FUNCTION
function parseCSV(str) {
    const arr = [];
    let quote = false;  
    let col = 0, c = 0, row = 0;
    arr[row] = []; arr[row][col] = "";

    for (; c < str.length; c++) {
        let cc = str[c], nc = str[c+1];
        if (cc == '"') { if (quote && nc == '"') { arr[row][col] += '"'; ++c; } else { quote = !quote; } }
        else if (cc == ',' && !quote) { ++col; arr[row][col] = ""; }
        else if (cc == '\r' && !quote && nc == '\n') { ++row; col = 0; arr[row] = []; arr[row][col] = ""; ++c; }
        else if (cc == '\n' && !quote) { ++row; col = 0; arr[row] = []; arr[row][col] = ""; }
        else if (cc == '\r' && !quote) { ++row; col = 0; arr[row] = []; arr[row][col] = ""; }
        else { arr[row][col] += cc; }
    }
    return arr;
}

// 3. CREATE THE MAP AUTOMATICALLY
const rows = parseCSV(rawCSV);
const loreMap = {};
for(let i=1; i<rows.length; i++) {
    const r = rows[i];
    if(r.length >= 4) {
        let name = r[0].trim();
        let lore = r[3];
        loreMap[name] = lore;
    }
}